    /**
     * fills the given StringBuilder with the sql where clausis constructed using the bean and the search type
     * @param sqlWhere the StringBuilder that will be filled
     * @param bean the bean to use for creating the where clausis
     * @param searchType exact ?  like ? starting like ?
     * @return the number of clausis returned
     */
    protected int fillWhere(StringBuilder sqlWhere, UserBean bean, int searchType)
    {
        if (bean == null) {
            return 0;
        }
        int _dirtyCount = 0;
        String sqlEqualsOperation = "=";
        if (searchType != SEARCH_EXACT) {
            sqlEqualsOperation = " like ";
        }
        try
        {
            if (bean.isCallDurationModified()) {
                _dirtyCount ++;
                if (bean.getCallDuration() == null) {
                    sqlWhere.append((sqlWhere.length() == 0) ? " " : " AND ").append("call_duration IS NULL");
                } else {
                    sqlWhere.append((sqlWhere.length() == 0) ? " " : " AND ").append("call_duration = ?");
                }
            }
            if (bean.isCallActionModified()) {
                _dirtyCount ++;
                if (bean.getCallAction() == null) {
                    sqlWhere.append((sqlWhere.length() == 0) ? " " : " AND ").append("call_action IS NULL");
                } else {
                    sqlWhere.append((sqlWhere.length() == 0) ? " " : " AND ").append("call_action = ?");
                }
            }
            if (bean.isReponseStatusModified()) {
                _dirtyCount ++;
                if (bean.getReponseStatus() == null) {
                    sqlWhere.append((sqlWhere.length() == 0) ? " " : " AND ").append("reponse_status IS NULL");
                } else {
                    sqlWhere.append((sqlWhere.length() == 0) ? " " : " AND ").append("reponse_status = ?");
                }
            }
            if (bean.isSubscriptionStatusModified()) {
                _dirtyCount ++;
                if (bean.getSubscriptionStatus() == null) {
                    sqlWhere.append((sqlWhere.length() == 0) ? " " : " AND ").append("subscription_status IS NULL");
                } else {
                    sqlWhere.append((sqlWhere.length() == 0) ? " " : " AND ").append("subscription_status = ?");
                }
            }
            if (bean.isMsisdnModified()) {
                _dirtyCount ++;
                if (bean.getMsisdn() == null) {
                    sqlWhere.append((sqlWhere.length() == 0) ? " " : " AND ").append("msisdn IS NULL");
                } else {
                    sqlWhere.append((sqlWhere.length() == 0) ? " " : " AND ").append("msisdn ").append(sqlEqualsOperation).append("?");
                }
            }
        }
        finally
        {
            sqlEqualsOperation = null;
        }
        return _dirtyCount;
    }

    /**
     * fill the given prepared statement with the bean values and a search type
     * @param ps the PreparedStatement that will be filled
     * @param bean the bean to use for creating the where clausis
     * @param searchType exact ?  like ? starting like ?
     * @return the number of clausis returned
     * @throws DAOException
     */
    protected int fillPreparedStatement(PreparedStatement ps, UserBean bean, int searchType) throws DAOException
    {
        if (bean == null) {
            return 0;
        }
        int _dirtyCount = 0;
        try
        {
            if (bean.isCallDurationModified()) {
                // System.out.println("Setting for " + _dirtyCount + " [" + bean.getCallDuration() + "]");
                if (bean.getCallDuration() == null) { ps.setNull(++_dirtyCount, Types.INTEGER); } else { Manager.setInteger(ps, ++_dirtyCount, bean.getCallDuration()); }
            }
            if (bean.isCallActionModified()) {
                // System.out.println("Setting for " + _dirtyCount + " [" + bean.getCallAction() + "]");
                if (bean.getCallAction() == null) { ps.setNull(++_dirtyCount, Types.TINYINT); } else { Manager.setInteger(ps, ++_dirtyCount, bean.getCallAction()); }
            }
            if (bean.isReponseStatusModified()) {
                // System.out.println("Setting for " + _dirtyCount + " [" + bean.getReponseStatus() + "]");
                if (bean.getReponseStatus() == null) { ps.setNull(++_dirtyCount, Types.TINYINT); } else { Manager.setInteger(ps, ++_dirtyCount, bean.getReponseStatus()); }
            }
            if (bean.isSubscriptionStatusModified()) {
                // System.out.println("Setting for " + _dirtyCount + " [" + bean.getSubscriptionStatus() + "]");
                if (bean.getSubscriptionStatus() == null) { ps.setNull(++_dirtyCount, Types.TINYINT); } else { Manager.setInteger(ps, ++_dirtyCount, bean.getSubscriptionStatus()); }
            }
            if (bean.isMsisdnModified()) {
                switch (searchType) {
                    case SEARCH_EXACT:
                        // System.out.println("Setting for " + _dirtyCount + " [" + bean.getMsisdn() + "]");
                        if (bean.getMsisdn() == null) { ps.setNull(++_dirtyCount, Types.VARCHAR); } else { ps.setString(++_dirtyCount, bean.getMsisdn()); }
                        break;
                    case SEARCH_LIKE:
                        // System.out.println("Setting for " + _dirtyCount + " [%" + bean.getMsisdn() + "%]");
                        ps.setString(++_dirtyCount, "%" + bean.getMsisdn() + "%");
                        break;
                    case SEARCH_STARTING_LIKE:
                        // System.out.println("Setting for " + _dirtyCount + " [" + bean.getMsisdn() + "%]");
                        ps.setString(++_dirtyCount, "%" + bean.getMsisdn());
                        break;
                    case SEARCH_ENDING_LIKE:
                        // System.out.println("Setting for " + _dirtyCount + " [%" + bean.getMsisdn() + "]");
                        if (bean.getMsisdn() + "%" == null) { ps.setNull(++_dirtyCount, Types.VARCHAR); } else { ps.setString(++_dirtyCount, bean.getMsisdn() + "%"); }
                        break;
                    default:
                        throw new DAOException("Unknown search type " + searchType);
                }
            }
        }
        catch(SQLException e)
        {
            throw new DataAccessException(e);
        }
        return _dirtyCount;
    }


